import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Icon} from '../../../../components';
import styled from "styled-components";
import { SelectUserLogin, SelectUserRole, selectUserSession } from '../../../../selectors';
import { ROLE } from '../../../../bff/session';
import { logout } from '../../../../actions';

const RightAligned = styled.div`
    display:    flex;
    justify-content:    flex-end;
    align-itrms:    center;

`;


const StyledBackIcon = styled.div`
    &:hover {
        cursor: pointer;
    }
`;

const StyledLogoutIcon = styled.div`
    margin: 0 0 10px 10px;
    &:hover {
        cursor: pointer;
    }
`;

const UserName = styled.div`
    font-size:  18ps;
    font-weight:    bold;

`;


const ControlPanelContainer = ({ className }) => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const roleId = useSelector(SelectUserRole);
    const login = useSelector(SelectUserLogin);
    const session = useSelector(selectUserSession);

    console.log(`roleId = ${roleId}`);

    return (
        <div className={className}>
            <RightAligned>


                    {roleId === ROLE.GUEST ? (
                        <Button>
                            <Link to="/login">Войти</Link>
                        </Button>
                    ) : (
                        <>
                            <UserName>{login}</UserName>

                        </>
                    )}

                {roleId !== ROLE.GUEST ? (
                    <StyledLogoutIcon onClick = {() => dispatch(logout(session))}>
                        <Icon id="fa-sign-out" margin="10px 0 0 10px" size="32px" />
                    </StyledLogoutIcon>
                ) : <></>}

            </RightAligned>
            <RightAligned>
                <StyledBackIcon onClick = {() => navigate(-1)}>
                    <Icon id="fa-backward" margin="10px 0 0 0" />
                </StyledBackIcon>

                <Link to="/post"><Icon id="fa-file-text-o" margin="10px 0 0 17px" /></Link>
                <Link to="/users"><Icon id="fa-users" margin="10px 0 0 17px" /></Link>


            </RightAligned>

        </div>    
    );    

};

export const ControlPanel = styled(ControlPanelContainer)`

`;
