import { useForm } from 'react-hook-form';
import { Link, Navigate } from 'react-router-dom';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';
import { server } from '../../bff/server';
import { useEffect, useState } from 'react';
import { Input, Button } from '../../components';

import styled from 'styled-components';
import { setUser } from '../../actions';
import { useDispatch, useSelector, useStore } from 'react-redux';
import { selectUserRole } from '../../selectors';
import { ROLE } from '../../bff/session';

const authFormSchema = yup.object().shape({
    login:  yup.string()
        .required('Введите логин')
        .matches(/^\w+$/, 'Неверный логин. Допускаются только буквы и цифры')
        .min(3, 'Неверный логин. Длина должна быть не менее 3 цифр')
        .max(15, 'Неверный логин. Длина не более 15 символов'),
    password:   yup.string()
        .required ('Введите пароль') 
        .matches(/^[\w#%]+$/, 'Неверный пароль. Допускаяются буквы, цифры и символы # %')
        .min(6, 'Длина пароля не менее 8 символов')  
        .max(15, 'Длина пароле не более 15 символов')


});

const StyledLink = styled(Link)`
    text-align: center;
    text-decoration:    underline;
    margin: 20px 0;
    font-size:  18px;
`;

const ErrorMessage = styled.div`
    font-size:  18px;
    margin: 10px 0 0;
    padding:    10px;
    background-color:   #fcadad;   
`;


const AutorizationContainer = ({ className }) => {
    const {
        register,
        reset,
        handleSubmit,
        formState:  { errors }

    } = useForm({
        defaultValues:  {
            login:  '',
            password:   '',
        },
        resolver:   yupResolver(authFormSchema),

    });

    const [serverError, setServerError] = useState(null);

    const store = useStore();
    const dispatch = useDispatch();

    const roleId = useSelector(selectUserRole);

    useEffect(() => {
        let currentWasLogout = store.getState().app.wasLogout;

        return store.subscribe(() => {
            let previousWasLogout = currentWasLogout;
            currentWasLogout = store.getState().app.wasLogout;

            if (currentWasLogout !== previousWasLogout) {
                reset();
            }
        });

    }, [reset, store]);

    const onSubmit = ({ login, password}) => {
        console.log(`in AutorizationContainer onSubmit login = ${login} password= ${password}`); 
        server.authorize(login, password).then(({error, res}) => {
            console.log(`in AutorizationContainer error = ${error}`);
            if (error) {  
                setServerError(`Ошибка запроса: ${error}`);
            }

            dispatch(setUser(res));
        })

    };

    const formError = errors.login?.message || errors?.password?.message ;

    const errorMessage = formError || serverError;

    if (roleId !== ROLE.GUEST) {
        return <Navigate to="/"></Navigate>

    }

    return (
        <div className={className}>
            <h2>Авторизация</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Input 
                    type="text" 
                    placeholder="Логин..." 
                    {...register('login', {
                        onChange: () => setServerError(null),
                    })} />
                <Input type="password" 
                    placeholder="Пароль..." 
                    {...register('password', {
                        onChange: () => setServerError(null),
                    })}
                />
                <Button type="submit" disabled={!!formError}>
                    Авторизоваться
                </Button>
                {errorMessage && <ErrorMessage>{errorMessage}</ErrorMessage>}
                <StyledLink to="/register">Регистрация</StyledLink>

            </form>


        </div>

    );
};

export const Autorization = styled(AutorizationContainer)`
    display:    flex;
    flex-direction: column;
    align-items:   center;

    & > form {
        display:    flex;
        flex-direction: column;
        width:  260px;
    }


`;
