import { useState } from "react";
import { useDispatch } from "react-redux";
import { Icon } from "../../../../components";
import styled from "styled-components";
import { TableRow } from "../table-row/table-row";

const UserRowContainer = ({ className, login, registredAt, roleId: userRoleId, roles }) => {

    const [selectedRoleId, setSelectedRoleId] = useState(userRoleId);
    const dispatch = useDispatch();

    // const roles = [];

    const onRoleChange = ({ target }) => {
        setSelectedRoleId(target.value);

    }

    return (
        <div className={className} >
            <TableRow border={true}>
                <div className="login-column">{login}</div>
                <div className="registered-at-column">{registredAt}</div>
                <div className="role-column">
                    <select value={selectedRoleId} onChange={onRoleChange}>
                        {roles.map(({ id: roleId, name: roleName }) => (
                            <option key={roleId} value={roleId}>{roleName}</option>
                        ))}
                    </select>
                    <div className="save-role-button">
                        <Icon id="fa-floppy-o"                    

                              onClick={() => dispatch(/* TODO */)} 
                        />
                    </div>

                </div>    
            </TableRow>

            <Icon id="fa-trash-o"                    
                margin="0 0 0 10px"
                onClick={() => dispatch(/* TODO */)} />
            <div className="user-data"></div>


        </div>
    );
};

export const UserRow = styled(UserRowContainer)`
    display:    flex;  
    margin-top: 10px;

    & select {
        padding: 0 5px;
        font-size:  16px;
    }

    & .save-role-button {
        width:  21px;
        hight:  32px;
        margin: 0 0 0 10px;
    }


`;
