import { ROLE } from "../constants";
import { getPost } from "../api";

export const fetchPost = async (postId) => {
    // const accessRoles = [ROLE.ADMIN];
    console.log(`in fetchPost`);
    const post = await getPost(postId);

    return {
        error:  null,
        res:    post,
    };
};
