export const sessions = {
    list:   {},
    create(user) {
        const hash = Math.random().toFixed(50);;
        this.list[hash] = user;

        return hash;
    },

    remove(hash) {
        delete this.list[hash];
    },

    access(userSession, accessRoles) {
        const user = sessions.list[userSession];

        return !!user && accessRoles.includes(user.roleId);
    },
}
