import { useServerRequest } from "../../hooks"; 
import { useState } from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import styled from "styled-components";


const MenuContainer = ({ className }) => {

    const [errorMessage, setErrorMessage] = useState(null);
    const [menuItems, setMenuItems] = useState(null);
    const requestServer = useServerRequest();
    console.log(requestServer)

    useEffect(() => {
        // Promise.all([
        //     requestServer('fetchMenu'), 

        // ]).then((menuRes) => {
            requestServer('fetchMenu').then((menuRes) => {
            console.log('in menu menuRes =', menuRes);
            if (menuRes.error) {
                console.log(`in menu menuRes = ${menuRes.error} errorMessage = ${menuRes.errorMessage}`);
                setErrorMessage(menuRes.error);
                return;
            };
            setMenuItems(menuRes.res[0].menu_items);
            console.log('menuItems', menuItems)

        });

    }, [requestServer]);




    return (
        <div className={className}>
            {/* <Content error={errorMessage}> */}
            <h2>Меню</h2>

            {menuItems ?

            <div className="menu-items">
               <ul>
                { 
                menuItems.map((item, index) => (
                    // <Link to={'/year'}>
                    <li className="menu-item" key={item.id}>
                        {/* <a href="/year"> */}
                            {item.menu_item}
                        {/* </a> */}
                    </li>
                )
                    // </Link>   
                )}  
                </ul>

            </div>
            : <>Пусто</> 

                }   
            {/* </Content> */}

        </div>    
    );

}

export const Menu = styled(MenuContainer)`
    display:    flex;
    flex-direction: column;  
    align-items:   center;
    margin: 0   auto;
    width:  570px;
    front-size: 28px;

    & .menu-item {
        margin: auto auto 10px;
        font-size:  large;
    }



`;
