import { ACTION_TYPE } from '../actions';
import { ROLE } from "../bff/session";

const initialUserState = {
    id: null,
    login:  null,
    roleId: ROLE.GUEST,
    session:    null,

};

export const userReducer = (state = initialUserState, action) => {
    switch (action.type) {
        case ACTION_TYPE.SET_USER: {
            return {
                ...state,
                ...action.paylosd,
            }
        }
        default:
            return state;
    }

};
