import { ACTION_TYPE } from "../actions/action-type";

const initialPostState = {
    id: '',
    title:  '',
    imageUrl:   '',
    content:    '',
    publishedAT:    '',
    comments:   [],

};

export const postReducer = (state = initialPostState, action) => {
    console.log(`in postReducer action.type = ${action.type}`);
    switch (action.type) {

        case ACTION_TYPE.SET_POST_DATA:

            return {
                ...state,
                ...action.payload,
            };
        default:
            return state;
    }

};
