export const getUser = async (loginToFind) => {
    console.log(`in getUser loginToFind = ${loginToFind}`);
    return fetch(`http://localhost:3005/users?login=${loginToFind}`)
        .then((loadedUser) => loadedUser.json())
        .then(([loadedUser]) => loadedUser);

}
