import { ROLE } from "../bff/session";

const initialUserState = {
    id: null,
    login:  null,
    roleId: ROLE.GUEST,
    session:    null,

};

export const userReducer = (state = initialUserState, action) => {
    switch (action.type) {
        case 'SET_SESSION': {
            return {
                ...state,
                session:    action.paylosd,
            }
        }
        default:
            return state;
    }

};
