export const sessions = {
    list:   {},
    create(user) {
        const hash = Math.random().toFixed(50);;
        this.list[hash] = user;

        return hash;
    },

    remove(hash) {
        delete this.list[hash];
    },

    access(hash, accessRoles) {
        console.log(`in sessions.access hash = ${hash} accessRoles = ${accessRoles}`)
        console.log(`n sessions.access this.list = ${this.list}`);
        const user = this.list[hash];
        if (!!user)
            console.log(`in sessions.access user.login = ${user.login} user.roleId = ${user.roleId}`)




        return !!user && accessRoles.includes(user.roleId);
    },
}
