import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { Button, Icon} from '../../../../components';
import styled from "styled-components";
import { SelectUserLogin, SelectUserRole } from '../../../../selectors';
import { ROLE } from '../../../../bff/session';
import { LOGOUT } from '../../../../actions';

const RightAligned = styled.div`
    display:    flex;
    justify-content:    flex-end;

`;


const StyledIcon = styled.div`
    cursor: pointer;
`;


const ControlPanelContainer = ({ className }) => {
    const navigate = useNavigate();
    const roleId = useSelector(SelectUserRole);
    const login = useSelector(SelectUserLogin);

    return (
        <div className={className}>
            <RightAligned>
                <Button>
                    {roleId === ROLE.GUEST ? (
                        <Link to="/login">Войти</Link>
                    ) : (
                        <>
                            <div>{login}</div>
                            <StyledIcon onClick = {() => dispatch(LOGOUT)}>
                                <Icon id="fa-sign-out" margin="10px 0 0 0" />
                            </StyledIcon>
                    </>
                    )}
                </Button>

            </RightAligned>
            <RightAligned>
                <StyledIcon onClick = {() => navigate(-1)}>
                    <Icon id="fa-backward" margin="10px 0 0 0" />
                </StyledIcon>

                <Link to="/post"><Icon id="fa-file-text-o" margin="10px 0 0 17px" /></Link>
                <Link to="/users"><Icon id="fa-users" margin="10px 0 0 17px" /></Link>


            </RightAligned>

        </div>    
    );    

};

export const ControlPanel = styled(ControlPanelContainer)`

`;
